def histogram(test):
    if not test:
        return {}

    words = test.split()
    letter_count = {}

    for word in words:
        for letter in word:
            letter_count[letter] = letter_count.get(letter, 0) + 1

    max_count = max(letter_count.values(), default=0)

    result = {letter: count for letter, count in letter_count.items() if count == max_count}

    return result

