def valid_date(date):
    if not date:
        return False

    try:
        month, day, year = map(int, date.split('-'))
    except ValueError:
        return False

    if len(date) != 10 or date[2] != '-' or date[5] != '-':
        return False

    if month < 1 or month > 12:
        return False

    days_in_month = [31, 28, 31, 30, 31, 30, 31, 31, 30, 31, 30, 31]

    if year % 4 == 0 and (year % 100 != 0 or year % 400 == 0):
